package week1.day1;

public class LoopAndConditional {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		
		for(i=1;i<=50;i++) {
			
			if(i%2!=0) {
				System.out.println(i + " is odd");
			}
			else 
				System.out.println(i + " is even");
		}
			}
		}
	


